mapper.select:
SELECT * FROM admin_user



    admin_state_flag = #{admin_state_flag}




ORDER BY admin_sign_date DESC

mapper.update:
update admin_user
set admin_state_flag = 'L' 
where admin_id = #{admin_id, jdbcType=VARCHAR}

update admin_user 
set admin_state_flag = 'S' 
where admin_id = #{admin_id, jdbcType=VARCHAR}

update admin_user 
set admin_state_flag = 'N' 
where admin_id = #{admin_id, jdbcType=VARCHAR}
